import React, {Component} from 'react';
import Particles from 'react-particles-js'
import Clarifai from 'clarifai'
import Navigation from './components/Navigation/Navigation';
import Logo from './components/Logo/Logo';
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm';
import Rank from './components/Rank/Rank';
import FaceRecognition from './components/FaceRecognition/FaceRecognition';
import './App.css';

const app = new Clarifai.App({
	apiKey: "7fe3f47b696348269f9d8f8fea852cb9"
})

const particlesOptions = {
  particles: {
    number: {
      value: 100,
      density:{
        enable: true,
        value_area: 800
      }
    }
  }
}

class App extends Component {
  constructor() {
    super();
    this.state = {
      input:'',
      imageURL:'',
      box: {}
    }
  }

  	caluculateFaceLocation = (data) => {
  	const clarifaiFace = data.outputs[0].data.regions[0].region_info.bounding_box;
  	const image = document.getElementById('imageinput');
  	const width = Number(image.width);
  	const height = Number(image.height);
  	console.log(width, height)
  }

  onInputChange = (event) => {
		this.setState({input:event.target.value})
  }

  onButtonSubmit = () => {
  	this.setState({imageURL:this.state.input})	//chanigin the data according to input the user put
  	app.models.predict(
  		Clarifai.FACE_DETECT_MODEL, 
  		this.state.input)
  	.then(response => this.caluculateFaceLocation(response))
  	.catch(err => console.log(err))
  }

  render(){
		return (
		<div className="App">
			<Particles className='particles'
				params={particlesOptions}
			/>
			<Navigation />
			<Logo />
			<Rank />
			<ImageLinkForm  
				onInputChange={this.onInputChange} 
				onButtonSubmit={this.onButtonSubmit}
			/>
			<FaceRecognition imageURL={this.state.imageURL}/>
		</div>
		);
  }
}
export default App;
