import React, {Component} from 'react';
import Particles from 'react-particles-js'
import FaceRecognition from './components/FaceRecognition/FaceRecognition';
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm';
import Navigation from './components/Navigation/Navigation';
import Register from './components/Register/Register';
import Signin from './components/Signin/Signin';
import Logo from './components/Logo/Logo';
import Rank from './components/Rank/Rank';
import './App.css';

const particlesOptions = {
	particles: {
		number: {
			value: 100,
			density:{
				enable: true,
				value_area: 800
			}
		}
	}
}

const initialState = {
	input:'',
	imageURL:'',
	box: {},
	route: 'signin',
	isSignedIn: false,
	user:{
		id: '',
		name: '',
		email: '',
		enteries: 0,
		joined: ''
	}
}

class App extends Component {
	constructor() {
		super();
		this.state = initialState;
		}

loadUser = (data) => {
    this.setState({user: {
      id: data.id,
      name: data.name,
      email: data.email,
      enteries: data.enteries,
      joined: data.joined
   }})
 }

	caluculateFaceLocation = (data) => {
		const clarifaiFace = data.outputs[0].data.regions[0].region_info.bounding_box;
		const image = document.getElementById('imageinput');
		const width = Number(image.width);
		const height = Number(image.height);
		return {
			leftCol: clarifaiFace.left_col * width,
			topRow: clarifaiFace.top_row * height,
			rightCol: width - (clarifaiFace.right_col * width),
			bottomRow: height - (clarifaiFace.bottom_row * height)
		}
	}

	displayFaceBox = (box) => {
		this.setState({box: box});
	}


	onInputChange = (event) => {
		this.setState({input:event.target.value})
	}

	onButtonSubmit = () => {
		this.setState({imageURL:this.state.input})	//changing the data according to the input that the user put
		etch('http://localhost:3000/image',{
					method: 'put',
					headers: {'Content-Type': 'application/json'},
					body: JSON.stringify({
						id: this.state.user.id,
						})
				})
		.then(response => {
			if (response) {
				fetch('http://localhost:3000/image',{
					method: 'put',
					headers: {'Content-Type': 'application/json'},
					body: JSON.stringify({
						id: this.state.user.id,
						})
				})
					.then(response => response.json())
					.then(count => {
						this.setState(Object.assign(this.state.user, {enteries: count}))
					})
					.catch(console.log)
			}
			this.displayFaceBox(this.caluculateFaceLocation(response))
		})
		.catch(err => console.log(err))
	}

	onRouteChange = (route) => {
		if (route === 'signout') {
			this.setState(initialState)
		} else if (route === 'home'){
			this.setState({isSignedIn: true})
		}
		this.setState({route: route});
	}

	render(){
		const {isSignedIn, imageURL, route, box} = this.state
		return (
		<div className="App">
			<Particles className='particles'
				params={particlesOptions}
			/>
			<Navigation isSignedIn={isSignedIn}onRouteChange={this.onRouteChange}/>
			{	route === 'home' 
				? <div>
						<Logo />
						<Rank name={this.state.user.name} enteries={this.state.user.enteries}/>
						<ImageLinkForm  
							onInputChange={this.onInputChange} 
							onButtonSubmit={this.onButtonSubmit}/>
						<FaceRecognition box={box} imageURL={imageURL}/>
					</div> 
				: (
					route === 'signin' 
					? <Signin loadUser={this.loadUser} onRouteChange={this.onRouteChange}/>
					: <Register loadUser={this.loadUser} onRouteChange={this.onRouteChange}/>
					) 
			}
		</div>
		);
	}
}
export default App;
